 once only per changeover	asl a	asl a	tay						;8 bytes per entry		lda sdma_data,y			;set initial destination	sta svramaddr	lda sdma_data+1,y	sta svramaddr+1		lda sdma_data+2,y		;set source	sta sdma_addr	lda sdma_data+3,y	sta sdma_addr+1		lda sdma_data+4,y	sta sdma_bank			;source bank		lda sdma_data+5,y	sta sdma_max			;number of lines to copy	lda sdma_data+6,y	sta sdma_size			;set line width lo	lda sdma_data+7,y	sta sdma_size+1			;set line width high	@keepgoing:			lda	svramaddr			;set current VRAM destination	sta	VMADDL	lda	svramaddr+1	sta	VMADDH			lda	#%00000001	sta	$4300			; dma mode				lda	#<VMDATAWL		; which channel for DMA	sta	$4301			; b-bus addr	lda	sdma_bank			sta	$4304			; bank #	lda	sdma_addr		;anim data lo	sta	$4302			; a-bus low addr	lda	sdma_addr+1		;anim data hi	sta	$4303			; a-bus high addr		lda sdma_size	sta	$4305			; # of bytes low	lda sdma_size+1	sta	$4306			; # of bytes high	lda	#1	sta	MDMAEN		; turn on dma		;update data pointers					inc sdmacount	lda sdmacount	cmp sdma_max			;max lines to copy	bcs @donetech			inc svramaddr+1		;inc dest addr by one row			SET16		lda sdma_addr			;inc source	clc	adc sdma_size	sta sdma_addr		SET8	plb	rtl	@donetech:	inc sdmaflag	stz sdmacount		plb	rtl	;-------------------------------------------; 	SDMA DATA FORMAT: 8 bytes/entry;;	destination (upper left);	source;	source bank #;	# of lines to download (1 line per cycle);	bytes per line (lo, hi);sdma_data:;	0 screaming hand	dc.w	$4000+(256*16)			;dest	dc.w	handobj0				;source	dc.b	abank						;bank			dc.b	4						;# of lines	dc.w	512						;bytes/line	;	2 crushing skull	dc.w	$4000+(256*16)			;dest	dc.w	headobj0				;source	dc.b	abank						;bank			dc.b	4						;# of lines	dc.w	512						;bytes/line;	4 giant sphere	dc.w	$4000+(256*16)			;dest	dc.w	ballobj0				;source	dc.b	abank						;bank			dc.b	4						;# of lines	dc.w	512						;bytes/line	;	6 ghost	dc.w	$4